     // Speed of wave advance in meters/sec
     float                     wave_speed;
     // Base visibility at 0 altitude, meters
     float                     visibility;
     // Base precipitation ratio at 0 altitude
     float                     precip_rate;
     // Climb rate due to thermals, meters/sec
